import React, { useState } from 'react';
import api from '../services/api';

import './User.page.css';

const User = (props) => {
  const [name, setName] = useState('');

  const onsubmit = () => {
    api
      .get(`search-user?user=${name}`)
      .then((res) => console.log('Dados do Usuário ' + res.data))
      .catch((err) => {
        alert(
          `Usuário ${name} não encontrado!\n verifique o nome desejado.\n${err}`
        );
      });
  };

  const renderSearch = () => {
    return (
      <div className="userPageSearchContainer">
        <label className="userPageSearchLabel">Nome de usuário</label>
        <input
          type="text"
          name="userSearch"
          className="userPageSearchInput"
          placeholder="@fahsoder"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <button className="userPageSearchSubmit" onClick={onsubmit}>
          Buscar
        </button>
      </div>
    );
  };

  return <div className="userPageContainer">{renderSearch()}</div>;
};

export default User;
